// SSRF Protection & Input Validation
const url = $input.item.json.url;
const selector = $input.item.json.selector || 'body';

// Validate URL
if (!url || !url.startsWith('http')) {
  throw new Error('Invalid URL');
}

// SSRF Protection
const blockedHosts = ['localhost', '127.0.0.1', '0.0.0.0', '169.254.169.254', 'metadata.google.internal'];
const urlObj = new URL(url);
if (blockedHosts.includes(urlObj.hostname)) {
  throw new Error('SSRF detected - blocked host');
}

return {
  json: {
    url,
    selector,
    requestId: `scrape-${Date.now()}`,
    timestamp: new Date().toISOString()
  }
};

// Extract content from HTML
// FIXED: Correct access to HTTP Request response
const httpResponse = $input.item.json;
const html = httpResponse.body || '';
const url = $('Input Validator').item.json.url;
const requestId = $('Input Validator').item.json.requestId;

try {
  // Extract title
  const titleMatch = /<title>(.*?)<\/title>/i.exec(html);
  const title = titleMatch ? titleMatch[1] : '';

  // Extract main content
  const mainRegex = /<main.*?>([\s\S]+?)<\/main>/i;
  const articleRegex = /<article.*?>([\s\S]+?)<\/article>/i;
  const bodyRegex = /<body.*?>([\s\S]+?)<\/body>/i;

  let mainContent = mainRegex.exec(html)?.[1] || 
                    articleRegex.exec(html)?.[1] || 
                    bodyRegex.exec(html)?.[1] || 
                    html;

  // Clean content
  mainContent = mainContent
    .replace(/<script[^>]*>.*?<\/script>/gis, '')
    .replace(/<style[^>]*>.*?<\/style>/gis, '')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim()
    .substring(0, 50000);

  return {
    json: {
      success: true,
      url,
      requestId,
      timestamp: new Date().toISOString(),
      data: {
        title,
        content: mainContent,
        content_length: mainContent.length,
        html_length: html.length
      }
    }
  };
} catch (error) {
  return {
    json: {
      success: false,
      url,
      requestId,
      error: error.message,
      timestamp: new Date().toISOString()
    }
  };
}

// Format error response
const url = $('Input Validator').item.json.url;
const requestId = $('Input Validator').item.json.requestId;
const statusCode = $input.item.json.statusCode || 0;
const statusMessage = $input.item.json.statusMessage || 'Unknown error';

return {
  json: {
    success: false,
    url,
    requestId,
    timestamp: new Date().toISOString(),
    error: {
      type: 'HTTP_ERROR',
      status: statusCode,
      message: statusMessage
    }
  }
};
